// styles
import "../../index.css";
import Styles from "./styles.module.css";

import React, { useRef } from "react";

const NewBlog = () => {
  const myRef = useRef({
    title: "",
    body: "",
    author: "",
  });

  function handleChangeForm(e) {
    myRef.current = {
      ...myRef.current,
      [e.target.name]: e.target.value,
    };
  }

  async function handleSubmit(e) {
    e.preventDefault();
    // Note: form behavior when click "Submit" will make page-reload, so use the line before.
    await fetch("http:localhost:7000/Blogs", {
      method: "POST",
      headers
    });
  }

  return (
    <div className={Styles.page}>
      <div className={Styles.newBlog}>
        <header>
          <h2>Add a new blog</h2>
        </header>
        <form onSubmit={handleSubmit}>
          {/* Note: handleSubmit is equal to (e) => handleSubmit(e) */}
          <label>
            Blog title
            <input
              type="text"
              name="name"
              placeholder="Blog Title"
              onChange={handleChangeForm}
            />
          </label>

          <label>
            Blog Author
            <input
              type="text"
              name="name"
              placeholder="Blogger Name"
              onChange={handleChangeForm}
            />
          </label>

          <label>
            Blog Body
            <textarea type="text" name="name" placeholder="Write a blog" />
          </label>

          <div className={Styles.formSubmit}>
            <button type="submit">Add Blog</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewBlog;
