// styles
import "../../index.css";
import Styles from "./styles.module.css";

const NewBlog = () => {
  return (
    <div className={Styles.newBlog}>
      <h2 >

      Add a new blog..
      </h2>
      <form>
        <label>
          Blog title:
          <input type="text" name="name" />
        </label>
        <label>
          Blog Body:
          <input type="text" name="name" />
        </label>
        <label>
          Blog Author:
          <input type="text" name="name" />
        </label>
        <button type="submit">Add Blog</button>
      </form>
    </div>
  );
};

export default NewBlog;
