// styles, icons
import "../../index.css";
import Styles from "./styles.module.css";
import {
  CiUser,
  CiStickyNote,
  CiSquareChevRight,
  CiEdit,
} from "react-icons/ci";

import { useState } from "react";

// custom hook
import useFetch from "../../hooks/useFetch";

// react-router-dom
import { Link } from "react-router-dom";

const Home = () => {
  const [searchedValue, setSearchedValue] = useState("");
  const [isEditable, setIsEditable] = useState(false);

  const {
    data: blogs,
    loading,
    error,
  } = useFetch("http://localhost:7000/Blogs");

  function handleEdit() {
    setIsEditable(true);
  }

  // const filteredBlogs = blogs?.filter((blog) =>
  //   blog?.title.toLowerCase().includes(searchedValue.toLowerCase())
  // );

  if (loading) return "Loading...";
  if (error) return "Error happened!";

  return (
    <>
      {/* search bar */}
      <div className={Styles.search}>
        <input
          type="text"
          placeholder="Search blog titles..."
          onChange={(e) => setSearchedValue(e.target.value)}
        />
      </div>

      {/* Blogs from API */}
      <div className={Styles.home}>
        {/* ToDo: the main thing i need is my search, so when search will back with a searched blogs not all blogs */}
        {/* {filteredBlogs?.length > 0
          ? filteredBlogs?.map((blog) => <Blog key={blog.id} blog={blog} />)
          : "no blogs!"} */}

        {/* here will be back with all blogs not the search one  */}
        {/* {blogs?.length > 0
          ? blogs?.map((blog) => <Blog key={blog.id} blog={blog} />)
          : "no blogs!"} */}

        {blogs?.length > 0
          ? blogs?.map((blog) => (
              <div className={Styles.blogCard}>
                <header>
                  <div className={Styles.titleContainer}>
                    <Link to={`/single/${blog.id}`} key={blog?.id}>
                      <h2>
                        <CiSquareChevRight className={Styles.icon} />

                        {isEditable ? (
                          <input type="text" defaultValue={blog.title} />
                        ) : (
                          <>{blog?.title}</>
                        )}
                      </h2>
                    </Link>

                    <CiEdit
                      className={Styles.iconEditTitle}
                      onClick={handleEdit}
                    />
                  </div>

                  <h3>
                    <CiUser className={Styles.icon} /> {blog?.author}
                  </h3>
                </header>
                <p>
                  <CiStickyNote className={Styles.iconPara} /> {blog?.body}
                </p>
              </div>
            ))
          : "No blogs!"}
      </div>
    </>
  );
};
export default Home;
