// react-redux
import { useDispatch, useSelector } from "react-redux";

import { useState, useEffect } from "react";

// styles and icons
import "../../index.css";
import Styles from "./styles.module.css";
import { CiUser, CiStickyNote, CiSquareChevRight } from "react-icons/ci";

// react-router-dom, react
import React from "react";
import { useNavigate, useParams } from "react-router-dom";

// react-helmet
import { Helmet } from "react-helmet";

// custom hook
// import useFetch from "../../hooks/useFetch";

export default function Single() {
  // sol-1: using useFetch:
  // const {
  //   data: blog,
  //   loading,
  //   error,
  // } = useFetch(`http://localhost:7000/Blogs/${id}`);

 

  const { id } = useParams();
  const nav = useNavigate();

  // stored blog
  const blog = useSelector((state) => state.blog);
  
  const dispatch = useDispatch();

  async function fetchData() {
    try {
      // API data request
      dispatch({ type: "FETCH_DATA_REQUEST" });

      const response = await fetch("http://localhost:7000/Blogs");
      const responseData = await response.json();

      // console.log("Response data: ", responseData);
      
      // Fetching data, Success
      dispatch({ type: "FETCH_DATA_SUCCESS", payload: responseData });
      
      

      // console.log("Blogs: ", blogs);
    } catch (error) {
      // Fetching data, Failure
      dispatch({ type: "FETCH_DATA_FAILURE", payload: error.message });
      console.log(error);
    }
  }
  
  try {
    const response =  fetch(`http://localhost:7000/Blogs/${id}`);
    const responseData = response.json();
    

    
    dispatch({ type: "FETCH_DATA_BLOG" , payload: responseData });

  } catch (error) {
    console.log(error);
  }

  

  function handleDelete() {
    fetch(`http://localhost:7000/Blogs/${id}`, { method: "DELETE" });
    nav("/");
  }

  function handleBackhome() {
    nav("/");
  }

  // sol-1: using useFetch
  // if (loading) return "Loading .....";
  // if (error) return "error .....";

  return (
    <div className={Styles.page}>
      <Helmet>
        <meta charSet="utf-8" />
        {/* <title>{`Blog - ${blog?.title}`}</title> */}
      </Helmet>

      <div className={Styles.card}>
        <header>
          <h2>
            <CiSquareChevRight className={Styles.icon} />
            {blog?.title}
            {/* {matchingTitle} */}
          </h2>
          <h3>
            <CiUser className={Styles.icon} />
            {/* {blog?.author} */}
          </h3>
        </header>

        <div className={Styles.para}>
          <p>
            <CiStickyNote className={Styles.iconPara} />
            {/* {blog?.body} */}
          </p>

          <div className={Styles.btns}>
            <button onClick={handleDelete}>Delete Blog</button>

            <button onClick={handleBackhome} className={Styles.backBtn}>
              Back to blogs
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
